import React from 'react';
import './DeckSelector.css';

const DeckSelector = ({ onDeckSelect }) => {
  const decks = [
    {
      id: 'acronyms',
      name: 'Acronyms',
      description: 'Test your knowledge of security acronyms and terminology',
      path: 'Acronyms/acs1.json',
      questionCount: 35,
      category: 'Terminology'
    },
    {
      id: 'exams',
      name: 'Practice Exams',
      description: 'Comprehensive practice questions covering all Security+ domains',
      path: 'Exams/exam1.json',
      questionCount: 90,
      category: 'Practice'
    }
  ];

  return (
    <div className="deck-selector">
      <div className="header">
        <h1>Security+ Flashcards</h1>
        <p>Choose a deck to start studying</p>
      </div>

      <div className="decks-grid">
        {decks.map((deck) => (
          <div
            key={deck.id}
            className="deck-card"
            onClick={() => onDeckSelect(deck)}
          >
            <div className="deck-icon">
              {deck.category === 'Terminology' ? 'üìö' : 'üìù'}
            </div>
            <h3>{deck.name}</h3>
            <p>{deck.description}</p>
            <div className="deck-meta">
              <span className="question-count">{deck.questionCount} questions</span>
              <span className="category">{deck.category}</span>
            </div>
            <div className="deck-hover-effect"></div>
          </div>
        ))}
      </div>

      <div className="footer">
        <p>Select a deck to begin your Security+ study session</p>
      </div>
    </div>
  );
};

export default DeckSelector;
