import React, { useState, useEffect } from 'react';
import './App.css';
import DeckSelector from './components/DeckSelector';
import Flashcard from './components/Flashcard';
import ProgressBar from './components/ProgressBar';

// Import JSON files directly
import acs1Data from '../Acronyms/acs1.json';
import exam1Data from '../Exams/exam1.json';

function App() {
  const [selectedDeck, setSelectedDeck] = useState(null);
  const [questions, setQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [showResults, setShowResults] = useState(false);
  const [answeredQuestions, setAnsweredQuestions] = useState(new Set());
  const [userAnswers, setUserAnswers] = useState({});

  useEffect(() => {
    if (selectedDeck) {
      loadQuestions();
    }
  }, [selectedDeck]);

  const loadQuestions = () => {
    try {
      let data;
      if (selectedDeck.id === 'acronyms') {
        data = acs1Data;
      } else if (selectedDeck.id === 'exams') {
        data = exam1Data;
      }

      if (data && data.questions) {
        setQuestions(data.questions);
        setCurrentQuestionIndex(0);
        setScore(0);
        setShowResults(false);
        setAnsweredQuestions(new Set());
        setUserAnswers({});
      }
    } catch (error) {
      console.error('Error loading questions:', error);
    }
  };

  const handleAnswer = (selectedOptions) => {
    const currentQuestion = questions[currentQuestionIndex];
    const isCorrect = JSON.stringify(selectedOptions.sort()) === JSON.stringify(currentQuestion.correct_answers.sort());

    setUserAnswers(prev => ({
      ...prev,
      [currentQuestionIndex]: selectedOptions
    }));

    if (isCorrect && !answeredQuestions.has(currentQuestionIndex)) {
      setScore(prev => prev + 1);
    }

    setAnsweredQuestions(prev => new Set([...prev, currentQuestionIndex]));
  };

  const nextQuestion = () => {
    if (currentQuestionIndex < questions.length - 1) {
      setCurrentQuestionIndex(prev => prev + 1);
    } else {
      setShowResults(true);
    }
  };

  const previousQuestion = () => {
    if (currentQuestionIndex > 0) {
      setCurrentQuestionIndex(prev => prev - 1);
    }
  };

  const resetQuiz = () => {
    setCurrentQuestionIndex(0);
    setScore(0);
    setShowResults(false);
    setAnsweredQuestions(new Set());
    setUserAnswers({});
  };

  const backToDeckSelection = () => {
    setSelectedDeck(null);
    setQuestions([]);
    setCurrentQuestionIndex(0);
    setScore(0);
    setShowResults(false);
    setAnsweredQuestions(new Set());
    setUserAnswers({});
  };

  if (!selectedDeck) {
    return <DeckSelector onDeckSelect={setSelectedDeck} />;
  }

  if (showResults) {
    return (
      <div className="app">
        <div className="results-container">
          <h1>Quiz Complete!</h1>
          <div className="score-display">
            <h2>Your Score: {score} / {questions.length}</h2>
            <p>Percentage: {Math.round((score / questions.length) * 100)}%</p>
          </div>
          <div className="results-buttons">
            <button onClick={resetQuiz} className="btn btn-primary">
              Try Again
            </button>
            <button onClick={backToDeckSelection} className="btn btn-secondary">
              Choose Different Deck
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (questions.length === 0) {
    return (
      <div className="app">
        <div className="loading">Loading questions...</div>
      </div>
    );
  }

  const currentQuestion = questions[currentQuestionIndex];
  const progress = ((currentQuestionIndex + 1) / questions.length) * 100;

  return (
    <div className="app">
      <div className="header">
        <button onClick={backToDeckSelection} className="back-button">
          ‚Üê Back to Decks
        </button>
        <h1>{selectedDeck.name}</h1>
        <div className="score">
          Score: {score} / {questions.length}
        </div>
      </div>

      <ProgressBar progress={progress} />

      <div className="question-counter">
        Question {currentQuestionIndex + 1} of {questions.length}
      </div>

      <Flashcard
        question={currentQuestion}
        onAnswer={handleAnswer}
        userAnswer={userAnswers[currentQuestionIndex]}
        isAnswered={answeredQuestions.has(currentQuestionIndex)}
      />

      <div className="navigation">
        <button
          onClick={previousQuestion}
          disabled={currentQuestionIndex === 0}
          className="btn btn-secondary"
        >
          Previous
        </button>

        <button
          onClick={nextQuestion}
          disabled={!answeredQuestions.has(currentQuestionIndex)}
          className="btn btn-primary"
        >
          {currentQuestionIndex === questions.length - 1 ? 'Finish' : 'Next'}
        </button>
      </div>
    </div>
  );
}

export default App;
